pragma solidity ^0.5.16;

contract Election {
  //Store candidate
  //read candidate
/*  string public candidate; //state variable, accessible inside our contract, represents the data
  //constructor
  constructor() public {
    candidate = "Candidate 1";
  }*/

  //model a candidate
  struct Candidate{
    uint id;
    string name;
    uint voteCount;
  }
  //  Store accounts that have voted.
  mapping(address=>bool) public voters;
  //Store candidates
  //Fetch candidates
  mapping(uint => Candidate) public candidates; //mapping is an associative array (key - value is structure type pair.
  // whenever we add a candidate to this mapping, we are changing a state of our contract and writing to the blockchain.

  //store candidates count
  uint public candidatesCount; //it is required because there is no way to determine the size of the mapping and there is no way to iterate over mapping either.
  //default value for uint is 0.

  // voted event
    event votedEvent (
        uint indexed _candidateId
    );

  constructor() public {
      addCandidate("Candidate 1");
      addCandidate("Candidate 2");
    }

    //add candidate function
    function addCandidate(string memory _name) private{  //private: except our contract, nobody will be to add a candidate. So we will call this function in constructor.
      candidatesCount ++;
      candidates[candidatesCount] = Candidate(candidatesCount,_name,0);
    }

    function vote(uint _candidateId) public {
      //require that they haven't voted before
      require(!voters[msg.sender]); //if the address is not present in the voters mapping that means it haven't voted before.

      //require a valid candidate
      //require(_candidateId > 0 && _candidateId <= candidatesCount);
      //record that voter has voted
      voters[msg.sender] = true;

      //update candidate voteCount
      candidates[_candidateId].voteCount++;

      // trigger voted event
        emit votedEvent(_candidateId);
    }
}
